INSERT INTO `blog` (`b_id`, `b_title`, `b_url_key`, `b_description`, `b_featured_image`, `b_status`, `b_publish_date`, `b_edit_date`) VALUES
(2, 'wow it solution_workshop\"today\"', 'my-first-blog', '<p>Our organization MothersonSumi INfotech &amp; Designs Limited (MIND) (&nbsp;<a href=\"http://www.mind-infotech.com/\" target=\"_blank\">www.mind-infotech.com</a>&nbsp;) is a joint venture of Samvardhana Motherson Group of India (founded in 1975) and Sumitomo Wiring Systems Ltd. (SWS) JAPAN (founded in 1917). We are a multinational, multilingual provider of IT solutions and services. MIND developed and deployed ERP solution for discrete manufacturing and process industries. Besides ERP MIND expertise and focus on development of customized application as per clien t requirement on any technology which helps its clients in reducing the cost of Information Technology and affect overall profitability as result of streamlining of information in business operation.</p>\r\n\r\n<p>We serve clients in more than 20 countries around the word. We are CMMI level 5 assessed, ISO 9001:2000 and ISO 27001 certified organization. We are Microsoft Gold Certified Partner, IBM Advanced Business Partner and Member of Oracle Partner Network.</p>\r\n\r\n<p>MIND is operating from offices situated in NOIDA at premises C-26, Sector 62 and D-14, Sector 59. We provide end to end Information Technology solutions having solutions offerings for Business Process automation, Bus iness Process Integration and Business Process Optimization in various engagement models to customers.</p>\r\n', 'QAYB1532936750.png', 'publish', '2018-07-30 00:00:00', '2018-07-31 07:00:00'),
(3, 'BNI Stellers blog', 'bni-stellers-blog', '<p>YOU ARE HIRED? These are the words you have been waiting to hear; here is your chance to bring them to life. Walk-in to HCL&rsquo;s MEGA RECRUITMENT DRIVE in Noida on&nbsp;<strong>August 04-05,2018</strong>&nbsp;and give your career a head start.</p>\r\n\r\n<p>HCL a leading global IT Giant is now giving an opportunity to graduates / post-graduates to be part of its global team in Noida. &nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>JOB OPPORTUNITIES FOR FRESHERS:</strong></p>\r\n\r\n<p><strong>ENGINEERING GRADUATES/ POST-GRADUATES:&nbsp;</strong>B.E./ B. Tech/ M.E./ M. Tech/ MCA/ M.Sc. (IT/CS) with min. 65% marks in graduation, 12th and 10th.</p>\r\n\r\n<p><strong>GRADUATES / POST-GRADUATES:</strong>&nbsp;BCA / B.Sc. / B.A./ M.A./ B. Com/ M. Com / BBA with min. 55% marks in graduation.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>The above positions are for local residents of Delhi, Noida, Greater Noida, Ghaziabad, Gurugram, and Faridabad.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>HIRING PROCESS ON THE DAY OF WALK-IN: &nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Briefing / Counselling: You will be briefed about HCL and its hiring process by HCL experts</li>\r\n	<li>Online Test:<br />\r\n	&nbsp; 1.&nbsp;<strong>Engineering Graduates / Post-Graduate</strong>s will have to appear for an online test comprising: English, Analytical Reasoning, Quantitative Ability and Computer Fundamentals<br />\r\n	&nbsp; 2. Other&nbsp;<strong>Graduates / Post-Graduates</strong>&nbsp;will have to appear for a test comprising: Grammar and V&amp;A (For Graduates only)</li>\r\n	<li>Interview: Post successful completion of the above process, you will have to appear for the interview&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>PS: Candidates are advised to carry hard copy of the latest resume, passport size photograph, and a local address proof like Aadhar Card.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"http://e.gtolink.in/t/em/9932/1/4b683e96-aecd-4749-b244-db8eb2d9b756/31\" target=\"_blank\">REGISTER FOR THE MEGA RECRUITMENT DRIVE &ndash; CLICK HERE</a><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>WALK-IN DETAILS:</strong></p>\r\n\r\n<p><strong>Venue:</strong>&nbsp;HCL Technologies Limited, SEZ, Plot No. 3A, Gate No. 4, Sector - 126, Noida.</p>\r\n\r\n<p><strong>Time:</strong>&nbsp;10:00 am &ndash; 4:00 pm</p>\r\n\r\n<p><strong>For Enquiries:</strong>&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Call Toll Free:</strong>&nbsp;1800-200-1118 (9:00 A.M &ndash; 6:00 P.M) or&nbsp;<strong>Give a Missed Call:</strong>&nbsp;8448386396.</p>\r\n\r\n<p><strong>Email:</strong>&nbsp;<a href=\"mailto:hcl-noida@hcl.com\" target=\"_blank\">hcl-noida@hcl.com</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Candidates selected will undergo fee-based training &amp; hiring program for skills enhancement. Details of the training &amp; hiring program are available on our&nbsp;<a href=\"http://e.gtolink.in/t/em/9932/1/4b683e96-aecd-4749-b244-db8eb2d9b756/32\" target=\"_blank\">HCL TSS</a>&nbsp;website and will also be shared during the Mega Recruitment Drive. Post successful completion of the training program, candidates will get assured jobs with HCL Technologies. Training and Job location will be Noida only. Selection of candidates is subject to them meeting the eligibility criteria and clearing the selection process.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Regards,</p>\r\n\r\n<p>Farha Ahmad</p>\r\n\r\n<p>Senior Executive HR Onboarding</p>\r\n\r\n<p>HCL Training &amp; Staffing Services</p>\r\n', 'POEO1533023213.jpg', 'publish', '2018-07-31 00:00:00', '2018-07-31 07:00:00'),
(4, 'blog todayy interestinggg wow it sol', 'blog-todayy-interestinggg', '<p>Dear Candidate,</p>\r\n\r\n<p>Greetings of the day!!!</p>\r\n\r\n<p>Hope you find this email in good spirit.</p>\r\n\r\n<p>Your Resume has been short-listed for an opening with Convergys India Services Pvt. Ltd., for</p>\r\n\r\n<p><strong>Customer Care Executives (Voice/Non-Voice)</strong></p>\r\n\r\n<p><strong>Technical Support Associate (Voice/Non-Voice)</strong></p>\r\n\r\n<p><strong>Sales Associate (Voice/Non-Voice)</strong></p>\r\n\r\n<p><strong>Quality Analyst(Non voice)</strong></p>\r\n\r\n<p><strong>Job Description:</strong></p>\r\n', 'GGYU1533192172.jpg', 'publish', '2018-08-02 00:00:00', '2018-08-02 07:00:00');

